mapper.select:

	select
	num, category, uName, title, views, writeTM
	from board where category = #{category}
	order by num desc limit #{start}, #{end}


	select
	num, category, uid, uName, title, content, views, writeTM
	from board where num = #{num}

mapper.update:

	update board set 
	category = #{category},
	title = #{title},
	content = #{content}
	where num = #{num}
